import { useEffect, useState } from "react";
import {Link} from "react-router-dom"
import Search from "./Search";

const SurfSpot = ({spots, search}) => {
const [spots1, setSpots] = useState([])

useEffect(() => {
    fetch('http://localhost:3000/spots')
    .then(r => r.json())
    .then((spots1) => setSpots(spots))
}, )

const filteredSpots = spots1.filter(spot => {
        return spot.title.includes(search)
    })
    console.log(filteredSpots)

    return (
        <div className="surf-spot"> 
        <Search spots={spots}/>
            {spots.map((spot) => (<div className="spot" key={spot.id}>
                <Link to={`/spots/${spot.id}`}>
                    <h2>{ spot.title }</h2>
                    <p> Location { spot.location }</p>
                </Link>
            </div>))}
        </div>
    );

    }
export default SurfSpot;
