    const updatedSpots = data.map(spot => {
     if (spot.id === updatedSpot.id) {
      return updatedSpot
     }
     else {
      return spot
     }
    })
    setData(updatedSpots)
  }

  return (
    <Router>
      <div className="App">
        <NavBar />
        <div className="content">
          <Switch>
            <Route exact path="/">
              <Home data={data} handleVisited={handleVisited}/>
            </Route>
            <Route path="/Gallery">
              <Gallery data={data} />
            </Route>
            <Route path="/NewSpot">
              <NewSpot handleAdd={handleAdd} />
            </Route>
            <Route path="/spots/:id">
              <SurfDetails handleDelete={handleDelete} data={data} />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
}

export default App;
