import React from 'react'
import {Link} from "react-router-dom"

const SurfCard = ({spot, handleVisited}) => {

    function handleClick(){
        fetch(`http://localhost:3000/spots/${spot.id}`, {
            method: 'PATCH',
            headers: {
              'Content-Type': 'application/json'
            },
            body: JSON.stringify({ visited: !spot.visited })

          })
            .then(res => res.json())
            .then(updatedSpot => {
              handleVisited(updatedSpot)
            })
    }

  return (
    <div className="spot">
    <Link to={`/spots/${spot.id}`}>
        <h2>{ spot.title }</h2>
        <p> Location { spot.location }</p>
    </Link>
    <button onClick={handleClick}>visited</button>
</div>
  )
}

export default SurfCard
