import {Link} from "react-router-dom"
import Search from "./Search";

const SurfSpot = ({spots, search}) => {

    const filteredSpots = spots.filter((spot) => {
        return spot.title.toLowerCase().includes(search.toLowerCase())
    })


    return (
        <div className="surf-spot"> 
        <Search spots={spots}/>
            {spots.map((spot) => (<div className="spot" key={spot.id}>
                <Link to={`/spots/${spot.id}`}>
                    <h2>{ spot.title }</h2>
                    <p> Location { spot.location }</p>
                </Link>
            </div>))}
        </div>
    );

    }
export default SurfSpot;
