import { useParams, useHistory } from "react-router-dom";
import { useEffect, useState } from "react";

const SurfDetails = ({ data, handleDelete }) => {
  const { id } = useParams();
  const [spot, setSpot] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const redirect = useHistory();

  useEffect(() => {
    if (data) {
      const foundSpot = data.find((spot) => spot.id === parseInt(id));
      if (foundSpot) {
        setSpot(foundSpot);
        setIsLoading(false);
      } else {
        setError("Spot not found.");
        setIsLoading(false);
      }
    }
  }, []);


  const handleClick = () => {
    fetch("http://localhost:3000/spots/" + spot.id, {
      method: "DELETE",
    }).then(() => {
      redirect.push("/");
    });
    handleDelete(id);
  };

  return (
    <div className="surf-details">
      {isLoading && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {spot && (
        <article>
          <h2>{spot.title}</h2>
          <p>Location {spot.location}</p>
          <div>{spot.body}</div>
          <button onClick={handleClick}>delete</button>
        </article>
      )}
    </div>
  );
};

export default SurfDetails;
